mapper.select:
SELECT 	user_email
FROM 	USERS
WHERE	user_email = #{user_email}

SELECT 	user_name
FROM 	USERS
WHERE	user_name = #{user_name}

SELECT user_email
FROM USERS
WHERE user_name = #{user_name}

SELECT *
FROM USERS
WHERE user_email = #{user_email}
AND user_name = #{user_name}

mapper.update:
UPDATE 	USERS
SET user_pw = #{user_pw}
WHERE user_email = #{user_email}

mapper.select:

SELECT *
FROM USERS
WHERE user_email = #{user_email}
AND user_pw = #{user_pw}
